var config = {
    lang: language,
    btns: [
        ['undo', 'redo'],
        ['strong', 'em', 'del'],
        ['link'],
        ['emoji'],
        ['removeformat']
    ]
};
$('body').on('click', '[ajax="textarea"]', function() {

    var $block =  $(this).closest('[ajax-selector="block block-form"]');

    $block.addClass('block-opened');

    $(this).trumbowyg(config);
    $block.find('.trumbowyg-editor').focus();
});

