window.onload=function(){$("html").removeClass("html-hidden"),$('[ajax-selector="dropdown"]').each(function(){if($(this).find('[ajax-selector="dropdown-menu"]').length){var d=$(this).find('[ajax-selector="dropdown-menu"]');d.offset().left+d.width()<=$("html").width()?d.removeClass("dropdown-edge"):d.addClass("dropdown-edge")}})};